  const handleDelete = async (id) => {
    await deleteNFT(id);
    setNfts((prevState) => prevState.filter((nft) => nft.id !== id));
  };

  return (
    <div>
      <Route path="/nfts/:id/edit">
        <NFTs nfts={nfts} handleDelete={handleDelete} />
      </Route>
      <Route path="/">
        <NFTs nfts={nfts} handleDelete={handleDelete} />
      </Route>
      <Route path="/">
        <NFTs nfts={nfts} handleDelete={handleDelete} />
      </Route>
      <Route path="/">
        <NFTs nfts={nfts} handleDelete={handleDelete} />
      </Route>
    </div>
  );
}
